import React from 'react';

const Form=()=>{
    function validate(e) {
    if(document.myForm.fname.value==''){
      alert( "Please provide your name!");
            document.myForm.Name.focus() ;
            return false;
         }
         if( document.myForm.email.value == "" ) {
          alert( "Please provide your Email!" );
          document.myForm.EMail.focus() ;
          return false;
       }
       if( document.myForm.psw.value == "" || isNaN( document.myForm.psw1.value ) ||
          document.myForm.psw.value.length != 5 ) {
          
          alert( "Password lenght must be 10 chartec." );
          document.myForm.Zip.focus() ;
          return false;
       }
         return( true );
        }
  
  function login(){
    document.getElementById('box').style.display='none'
    document.getElementById('s3').style.display='block'
  }
  function signup(){
    document.getElementById('box').style.display='block'
    document.getElementById('s3').style.display='none'
  }
    return(
      
        <body>
         
        <article id='s2'>
    <form id='box' onSubmit={validate} name='myForm'>
      <figure>
    <i class="fa fa-facebook-official" id='icon'></i>
    <i class="fa fa-twitter-square" id='icon'></i>
    <i class="fa fa-instagram" id='icon'></i>
    <i class="fa fa-google-plus-square" id='icon'></i>
    <i class="fa fa-reddit-square" id='icon'></i></figure>
<label>
<p id='p'>UserName</p>
<i class="fa fa-user" id="user"></i>
<input type='text' placeholder='Enter Name' id='Text' name='fname'/>
</label>
<label>
<p>Email</p>
<i class="fa fa-envelope" id='user'></i>
<input type='email' placeholder='Enter Email' id='Text' name='email'/>
</label>
<label>
<p>Password</p>
<i class="fa fa-key" id='user'></i>

<input type='password' placeholder='Enter Password' id='Text' name='psw'/>
</label>
<label>
  <p>Confirm Password</p>
  <i class="fa fa-key" id='user'></i>

  <input type='password' placeholder='Confirm Password' id='Text' name='psw1'/>
  </label>
<br></br>

<input type='submit' value='SignUp' id='btn'/>
<center><p>Already have an account? <a href="#" onClick={login}>Login</a></p></center>
    </form>
  
</article>
<article id='s3'>
<label>
<p>Email</p>
<i class="fa fa-envelope" id='user'></i>
<input type='email' placeholder='Enter Email' id='Text'/>
</label>
<label>
<p>Password</p>
<i class="fa fa-key" id='user'></i>

<input type='password' placeholder='Enter Password' id='Text'/>
</label>
<br></br>

<input type='submit' value='Login' id='btn'/>
<center><p>Don't have an account ? <a href="#" onClick={signup}>Sign up now</a></p></center>
</article>
</body>

    );
}
export default Form;
