import React from 'react'
import './Nav.css'
import {NavLink} from 'react-router-dom'
import {useMediaQuery} from 'react-responsive'
import twitter from './twitter.jpg'
const Nav=()=>{
  
    function click(){
        var x=document.getElementById('form')
        if(x.style.display==='none'){
          x.style.display='block'        
        }
           else{
          x.style.display='none'
        }
       }
       
      function side(){
          document.getElementById('resposiveright').style.display='flex'
          document.getElementById('resposiveright').style.flexDirection='column'
          document.getElementById('open').style.display='none'
document.getElementById('close').style.display='flex'
document.getElementById('close').style.justifyContent='flex-end'
          }
      function side1(){
        document.getElementById('resposiveright').style.display='none'
        document.getElementById('open').style.display='block'
        document.getElementById('close').style.display='none'
      
      }
      const Desktop=({children})=>{
        const isDesktop=useMediaQuery({minWidth:992})
        return isDesktop?children:null
      }
      const Mobile=({children})=>{
        const isMobile=useMediaQuery({maxWidth:766})
        return isMobile?children:null

      }
     const twt={
       width:'130px',
       borderRadius:'50%',
       padding:'20px'
     };
     const h2=()=>{
       document.getElementById('h2').innerHTML=window.location.replace('http://localhost:3000/About')
       document.getElementById('resposiveright').style.display='none'
       document.getElementById('close').style.display='none'
       document.getElementById('open').style.display='block'
     }
     const h3=()=>{
      document.getElementById('h3').innerHTML=window.location.replace('http://localhost:3000/Service')
      document.getElementById('resposiveright').style.display='none'
      document.getElementById('close').style.display='none'
      document.getElementById('open').style.display='block'
    }
    const h4=()=>{
      document.getElementById('h4').innerHTML=window.location.replace('http://localhost:3000/Contact')
      document.getElementById('resposiveright').style.display='none'
      document.getElementById('close').style.display='none'
      document.getElementById('open').style.display='block'
    }
    const h1=()=>{
      document.getElementById('h1').innerHTML=window.location.replace('http://localhost:3000/')
      document.getElementById('resposiveright').style.display='none'
      document.getElementById('close').style.display='none'
      document.getElementById('open').style.display='block'
    }
    const fbook=()=>{
      document.getElementById('fbook').innerHTML=window.location.replace('https://www.facebook.com/programmer.me1/')
    }
    return(
        <>
     <Desktop>
        <section className='navbar'>
            <div className='navbar1'>
                <a href='http://localhost:3000/' style={{textDecoration:'none',color:'black'}}>HariCharan</a>
            </div>
            <div className='navbar2'>
         <NavLink to ='/' style={{textDecoration:'none',color:'rgb(85, 17, 85)'}} >Home</NavLink>
         <NavLink to ='/About'  style={{textDecoration:'none',color:'rgb(85, 17, 85)'}}>About</NavLink>
 <NavLink to ='/Service' style={{textDecoration:'none',color:'rgb(85, 17, 85)'}}>Service</NavLink>
 <NavLink to ='/Contact' style={{textDecoration:'none',color:'rgb(85, 17, 85)'}}>Contact</NavLink>
            </div>
          <div className='navbar3'>
        <button onClick={click} className='login'>Login/Signup</button>
            </div>
          
        </section>
      </Desktop>
      
      <Mobile>
      <section className='resposive'>
            <div className='resposive-center'>
                  <i className="fa fa-bars" onClick={side} id='open'></i>
                <i className="fa fa-close" id='close' onClick={side1}></i>
             <center> <a href='http://localhost:3000/' style={{textDecoration:'none',color:'black',fontSize:'35px',marginLeft:'10px'}} >HariCharan</a>
               </center>
            </div>
          <div className='resposive-button'>
        
        </div>
        </section>
        <section>
        <div id='resposiveright'>
<p style={{borderBottomColor:'1px solid white'}}>
 <center> <img src={twitter } style={twt}/></center>
 <i class='fa fa-facebook' id='fbook' onclick={fbook}/>
</p><center>
         <NavLink to ='/' style={{textDecoration:'none',color:'white'}} id='h1' onClick={h1}>Home</NavLink>
         <NavLink to ='/About'  style={{textDecoration:'none',color:'white'}} id='h2' onClick={h2}>Course</NavLink>
 <NavLink to ='/Service' style={{textDecoration:'none',color:'white'}} id='h3' onClick={h3}>Service</NavLink>
 <NavLink to ='/Service' style={{textDecoration:'none',color:'white'}} id='h4' onClick={h4}>Contact</NavLink>
</center>
            </div>
        </section>
      </Mobile>
       
       
        
       </> 
          
    );
}
export default Nav;
