import React,{Component} from 'react'
import Footer from './Footer'
import {useMediaQuery} from 'react-responsive'
import './footer.css'
class ResFooter extends Component{
    render(){
const Mobile=({children})=>{
    const isMobile=useMediaQuery({minWidth:200,maxWidth:766})
     return isMobile?children:null
}
const Desktop=({children})=>{
    const isDesktop=useMediaQuery({minWidth:992})
     return isDesktop?children:null
}
const Tablet=({children})=>{
    const isTablet=useMediaQuery({minWidth:767,maxWidth:1023})
     return isTablet?children:null
}
   return(
        <>
        <Desktop>
            <div className='Desktop'>
                <Footer/>
            </div>
       </Desktop>
        
        <Mobile>
        <div className='Mobile'>
        <Footer/>
        </div>
        </Mobile>
        
        
        <Tablet>
        <div className='Tablet' style={{backgroundColor:'yellow'}}>
        <Footer/>
        </div>
        </Tablet>
        
       
</>
    );
    
}}

export default ResFooter;
